import React, { useState } from "react";
import { checkPassword, validateEmail } from "../../utils/helpers";


function Contact() {
    //set variables for use in form
    const [email, setEmail] = useState('');
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [errMessage, setErrMessage] =useState('');

    const inputHandle = (e) => {
        //get the values of the input
        const { target } = e;
        const inputType = target.name;
        const inputValue = target.value;

        //set values
        if (inputType === 'email') {
            setEmail(inputValue);
        } else if (input === 'userName') {
            setUserName(inputValue);
        } else if (input === 'password') {
            setPassword(inputValue);
        }
    };

    const submitHandle = (e) => {
        e.preventDefault();

        //validators
        if (!validateEmail(email) || !userName) {
            setErrMessage('email or username is invalid');
            return;
        }
        if (!checkPassword(password)) {
            setErrMessage(`please choose a more secure password for ${userName}`);
            return;
        }

        //reset field values
        setUserName('');
        setPassword('');
        setEmail('');
        alert(`tallyho, ${userName}`);
    };

    const submit = 'submit -->'

    return (
        <div>
            <form className="form">
                <input value={email} name="email" onChange={inputHandle} type="email" placeholder="email" />
                <input value={userName} name="userName" onChange={inputHandle} type="userName" placeholder="username" />
                <input value={password} name="password" onChange={inputHandle} type="password" placeholder="password" />
                <button type="button" onClick={submitHandle}>{submit}</button>
            </form>
            {errMessage && (
                <div>
                    <p className="error-text">{errMessage}</p>
                </div>
            )}
        </div>
    )
};

export default Contact;
